import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './ProductList.css'; // Import CSS for styling

const ProductList = ({ searchQuery }) => {
  const [products, setProducts] = useState([]);

  // Dummy data for product list
  const allProducts = [
    { id: 1, title: 'Product 1', imageUrl: '/images/product1.jpg', price: '$29.99' },
    { id: 2, title: 'Product 2', imageUrl: '/images/product2.jpg', price: '$49.99' },
    { id: 3, title: 'Product 3', imageUrl: '/images/product3.jpg', price: '$19.99' },
    // Add more products as needed
  ];

  useEffect(() => {
    // Filter products based on search query
    if (searchQuery) {
      const filteredProducts = allProducts.filter(product =>
        product.title.toLowerCase().includes(searchQuery.toLowerCase())
      );
      setProducts(filteredProducts.slice(0, 10)); // Limit to top 10 products
    } else {
      setProducts(allProducts.slice(0, 10)); // Show top 10 products if no search query
    }
  }, [searchQuery]);

  return (
    <div className="product-list">
      {products.map(product => (
        <Link key={product.id} to={`/product/${product.id}`} className="product-card">
          <img src={product.imageUrl} alt={product.title} className="product-image" />
          <h3>{product.title}</h3>
          <p>{product.price}</p>
        </Link>
      ))}
    </div>
  );
};

export default ProductList;
