import React, { useState } from "react";
import SearchBar from "../components/SearchBar";
import ProductList from "../components/ProductList";

const mockProducts = [
  { id: 1, name: "Product 1", description: "Description 1", image: "/logo192.png", price: 100 },
  { id: 2, name: "Product 2", description: "Description 2", image: "/logo192.png", price: 200 },
  { id: 3, name: "Product 3", description: "Description 3", image: "/logo192.png", price: 300 },
  // Add more mock products here if needed
];

function HomePage({ searchQuery, setSearchQuery }) {
  const [products, setProducts] = useState(mockProducts);

  const filteredProducts = products.filter((product) =>
    product.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div>
      <h1>Welcome to E-commerce App</h1>
      <SearchBar setSearchQuery={setSearchQuery} />
      <ProductList products={filteredProducts} />
    </div>
  );
}

export default HomePage;
