import { useState, useEffect } from "react";
import { initializeApp } from "firebase/app";
import { getAuth } from "firebase/auth";
import { getAnalytics } from "firebase/analytics";

// Your Firebase configuration
const firebaseConfig = {
  apiKey: "AIzaSyDdvkWcfXeuNvsDJ7D9MbLv3KBsI66lx10",
  authDomain: "udaysai3204.firebaseapp.com",
  projectId: "udaysai3204",
  storageBucket: "udaysai3204.firebasestorage.app",
  messagingSenderId: "816479225374",
  appId: "1:816479225374:web:72fb367440085ff06c500f",
  measurementId: "G-EHDJ08S4KP"
};

let app, auth, analytics;

const FirebaseInitializer = ({ children }) => {
  const [firebaseInitialized, setFirebaseInitialized] = useState(false);

  useEffect(() => {
    try {
      // Initialize Firebase only once when the component mounts
      app = initializeApp(firebaseConfig);
      auth = getAuth(app);
      analytics = getAnalytics(app);
      setFirebaseInitialized(true); // Mark Firebase as initialized
    } catch (error) {
      console.error("Firebase initialization error", error);
    }
  }, []);

  if (process.env.NODE_ENV === 'production') {
    const app = initializeApp(firebaseConfig);
  }

  if (!firebaseInitialized) {
    return <div>Loading Firebase...</div>; // Show a loading indicator until Firebase is ready
  }

  return children; // Render your app once Firebase is initialized
};

export { FirebaseInitializer, auth, analytics };
