import React, { useState, useEffect } from "react";
import ProductCard from "./ProductCard"; // Import the ProductCard component
import "./ProductList.css"; // Import CSS for styling

const ProductList = ({ searchQuery }) => {
  const [products, setProducts] = useState([]);

  // Dummy data for product list
  const allProducts = [
    { id: 1, title: "Product 1", imageName: "product1.jpg", price: "$29.99" },
    { id: 2, title: "Product 2", imageName: "product2.jpg", price: "$49.99" },
    { id: 3, title: "Product 3", imageName: "product3.jpg", price: "$19.99" },
    // Add more products as needed
  ];

  useEffect(() => {
    // Filter products based on search query
    const filteredProducts = searchQuery
      ? allProducts.filter((product) =>
          product.title.toLowerCase().includes(searchQuery.toLowerCase())
        )
      : allProducts; // Show all products if no search query

    setProducts(filteredProducts.slice(0, 10)); // Limit to top 10 products
  }, [searchQuery]); // Re-run whenever searchQuery changes

  return (
    <div className="product-list">
      {products.length === 0 ? (
        <p>No products found</p>
      ) : (
        products.map((product) => (
          <ProductCard
            key={product.id}
            id={product.id}
            title={product.title}
            imageName={product.imageName} // Pass the imageName dynamically
            price={product.price}
          />
        ))
      )}
    </div>
  );
};

export default ProductList;
