import { useState, useEffect } from "react";
import SearchBar from "../components/SearchBar";
import ProductList from "../components/ProductList";

const mockProducts = [
  { id: 1, name: "Product 1", description: "Description 1", image: "/logo192.png", price: 100 },
  { id: 2, name: "Product 2", description: "Description 2", image: "/logo192.png", price: 200 },
  { id: 3, name: "Product 3", description: "Description 3", image: "/logo192.png", price: 300 },
  { id: 4, name: "Product 4", description: "Description 4", image: "/logo192.png", price: 400 },
  { id: 5, name: "Product 5", description: "Description 5", image: "/logo192.png", price: 500 },
  // Add more mock products if needed
];

function HomePage({ searchQuery }) {
  const [products, setProducts] = useState(mockProducts);

  // Filter products based on searchQuery
  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div>
      <h1>Welcome to E-commerce App</h1>
      <SearchBar setSearchQuery={(query) => setSearchQuery(query)} />
      <ProductList products={filteredProducts} />
    </div>
  );
}

export default HomePage;
