import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Login from "./components/Login";
import Signup from "./components/Signup";
import Home from "./pages/Home";
import Dashboard from "./pages/Dashboard";
import Navbar from "./components/Navbar";
import LogoutButton from "./components/LogoutButton"; // Import the logout button
import { useState, useEffect } from "react";
import { auth } from "./services/firebaseConfig"; // Import the auth object from firebaseConfig
import { onAuthStateChanged } from "firebase/auth"; // Firebase method to check if the user is logged in

function App() {
  const [user, setUser] = useState(null);

  // Check if the user is logged in or not
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser); // Update the state with the current user
    });

    // Cleanup the listener on unmount
    return () => unsubscribe();
  }, []);

  return (
    <Router>
      <Navbar />
      {/* Conditionally render the LogoutButton if the user is logged in */}
      {user && <LogoutButton />}
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/dashboard" element={<Dashboard />} />
      </Routes>
    </Router>
  );
}

export default App;
